import React from "react";
import "./Navbar.css";

const Navbar = () => (
    <div>
    <ul className="nav nav-pills nav-justified">
        <li><a href="/">Click-a-Cat!</a></li>
        <li
            className={this.props.message.indexOf('incorrectly') !== -1 ? 
                "desc-incorrect" : 
                this.props.message.indexOf('correctly') !== -1 ?
                    "desc-correct" :
                    "desc-normal"} >
            {this.props.message} 
        </li>
        <li>Score: <span style={{color: "yellow"}}>{this.props.curScore}</span> | Top Score: {this.props.topScore}</li>
    </ul>
</div>
);

export default Navbar;
